package javapro;

public class VariableDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declaring a variable with value
		//NUMBER TYPES
		
		byte amoeba=10; //bit
		
		amoeba=-128; //highest negative range
		amoeba=120; //highest positive range 
		
		short dwarf=20; //16 bit
		
		dwarf=-32768; //highest negative range 
		dwarf=32767;//highest positive range
		
		int lottery=1010292;//32 bit
		
		lottery=-2147483648; //highest negative range 
		lottery=2147483647; //highest positive range
		
		long distance=3545435435354353L;//64 bit
		
		distance=-9223372036854775808L; //highest negative range 
		distance=9223372036854775807L; //highest positive range
	}

		
	}


